mapper.select:
SELECT
    id,
    goods_name,
    goods_price,
    origin_price,
    goods_images,
    detail_images,
    goods_type,
    comment_amount,
    collect_amount,
    deal_amount,
    add_time,
    is_sale
FROM t_goods g

    AND g.id = #{id}
    AND g.goods_name = #{goodsName}
    AND g.goods_price = #{goodsPrice}
    AND g.origin_price = #{originPrice}
    AND g.goods_images = #{goodsImages}
    and g.detail_images = #{detailImages}
    AND g.goods_type = #{goodsType}
    AND g.comment_amount = #{commentAmount}
    AND g.collect_amount = #{collectAmount}
    AND g.deal_amount = #{dealAmount}
    AND g.add_time = #{addTime}
    AND g.is_sale = #{isSale}

SELECT
    id,
    goods_name,
    goods_price,
    origin_price,
    goods_images,
    detail_images,
    goods_type,
    comment_amount,
    collect_amount,
    deal_amount,
    add_time,
    is_sale
FROM t_goods g

    AND g.id = #{id},
    AND g.goods_name = #{goodsName}
    AND g.goods_price = #{goodsPrice}
    AND g.origin_price = #{originPrice}
    AND g.goods_images = #{goodsImages}
    AND g.detail_images = #{detailImages}
    AND g.goods_type = #{goodsType}
    AND g.comment_amount = #{commentAmount}
    AND g.collect_amount = #{collectAmount}
    AND g.deal_amount = #{dealAmount}
    AND g.add_time = #{addTime}
    AND g.is_sale = #{isSale}

ORDER BY g.add_time DESC
limit #{page.startIndex},#{page.offset}

SELECT
id,
